Scripts.OnLoad:
 
            this:RegisterEvent("VARIABLES_LOADED");
			this:RegisterEvent("ADDON_LOADED");
			this:RegisterEvent("AUCTION_ITEM_LIST_UPDATE");
			this:RegisterEvent("AUCTION_OWNED_LIST_UPDATE");
			this:RegisterEvent("AUCTION_HOUSE_SHOW");
			this:RegisterEvent("AUCTION_HOUSE_CLOSED");
			this:RegisterEvent("NEW_AUCTION_UPDATE");
			this:RegisterEvent("CHAT_MSG_ADDON");
			this:RegisterEvent("WHO_LIST_UPDATE");
			
			self.TimeSinceLastUpdate = 0;

Scripts.OnShow:
if (self.withMask) then
	Atr_Mask:Show();
end
Atr_Batch_Cancel:Disable();
Atr_Batch_OK:Disable();

Scripts.OnHide:
if (self.withMask) then
	Atr_Mask:Hide();
	self.withMask = nil;
end
Atr_Batch_Cancel:Enable();
Atr_Batch_OK:Enable();

Scripts.OnChar:
if ( not self:IsInIMECompositionMode() ) then
	Atr_Item_Autocomplete(self);
end
