    local itemcolor = CT_RaidTracker_RaidLog[self.raidid]["Loot"][self.itemid]["item"]["c"];
    dwGetglobal(self:GetName() .. "Editing"):SetText("编辑 \"|c" .. itemcolor .. "" .. itemname .. "|r\" 的DKP");

    itemID = CT_RaidTracker_RaidLog[self.raidid]["Loot"][self.itemid]["item"]["id"];
    text = CT_RaidTracker_Items[itemID];
