Scripts.OnMouseUp:
if ( this.sortType and this.func ) then
	this.func(this.sortType);
end

self.widget.OnClick()
if button~="RightButton" then return end
MiDKP_UIHelp.Util.RightButtonUp(self, button,"event");

self.widget.OnClick()
if button~="RightButton" then return end
MiDKP_UIHelp.Util.RightButtonUp(self, button,"item");

self.widget.OnClick()
if button~="RightButton" then return end
MiDKP_UIHelp.Util.RightButtonUp(self, button,"member");


self.widget.OnClick()
if button~="RightButton" then return end
MiDKP_UIHelp.Util.RightButtonUp(self, button,"dkp");

Scripts.OnEnter:
if (not self.disabled) then
	local textOb = getglobal(self:GetName().."Text");
	textOb:SetTextColor(1.0, 1.0, 1.0);
end

Scripts.OnLeave:
				if (not self.disabled) then
					local textOb = getglobal(self:GetName().."Text");

					if (textOb.color) then
						textOb:SetTextColor(textOb.color.r, textOb.color.g, textOb.color.b, textOb.color.a);
					else
						textOb:SetTextColor(1.0, 0.82, 0);
					end
				end

Scripts.OnLoad:
				self:SetAttribute("UIPanelLayout-defined", true);
				self:SetAttribute("UIPanelLayout-area", "center");
				self:SetAttribute("UIPanelLayout-pushable", 0);
				self:SetAttribute("UIPanelLayout-enabled", true);


				self:SetBackdropBorderColor(TOOLTIP_DEFAULT_COLOR.r, TOOLTIP_DEFAULT_COLOR.g, TOOLTIP_DEFAULT_COLOR.b);
				self:SetBackdropColor(TOOLTIP_DEFAULT_BACKGROUND_COLOR.r, TOOLTIP_DEFAULT_BACKGROUND_COLOR.g, TOOLTIP_DEFAULT_BACKGROUND_COLOR.b);
				MiDKPRaidPage:ClearAllPoints();
				MiDKPRaidPage:SetParent(self);
				MiDKPRaidPage:SetPoint("TOPLEFT", MiDKPMainFrame, "TOPLEFT", 0, 0);
				MiDKPRaidPage:SetPoint("BOTTOMRIGHT", MiDKPMainFrame, "BOTTOMRIGHT", 0, 0);

				MiDKPMemberPage:ClearAllPoints();
				MiDKPMemberPage:SetParent(self);
				MiDKPMemberPage:SetPoint("TOPLEFT", MiDKPMainFrame, "TOPLEFT", 0, 0);
				MiDKPMemberPage:SetPoint("BOTTOMRIGHT", MiDKPMainFrame, "BOTTOMRIGHT", 0, 0);

				MiDKPEventPage:ClearAllPoints();
				MiDKPEventPage:SetParent(self);
				MiDKPEventPage:SetPoint("TOPLEFT", MiDKPMainFrame, "TOPLEFT", 0, 0);
				MiDKPEventPage:SetPoint("BOTTOMRIGHT", MiDKPMainFrame, "BOTTOMRIGHT", 0, 0);

				MiDKPItemPage:ClearAllPoints();
				MiDKPItemPage:SetParent(self);
				MiDKPItemPage:SetPoint("TOPLEFT", MiDKPMainFrame, "TOPLEFT", 0, 0);
				MiDKPItemPage:SetPoint("BOTTOMRIGHT", MiDKPMainFrame, "BOTTOMRIGHT", 0, 0);

				MiDKPOptionPage:ClearAllPoints();
				MiDKPOptionPage:SetParent(self);
				MiDKPOptionPage:SetPoint("TOPLEFT", MiDKPMainFrame, "TOPLEFT", 0, 0);
				MiDKPOptionPage:SetPoint("BOTTOMRIGHT", MiDKPMainFrame, "BOTTOMRIGHT", 0, 0);

				MiDKPDKPPage:ClearAllPoints();
				MiDKPDKPPage:SetParent(self);
				MiDKPDKPPage:SetPoint("TOPLEFT", MiDKPMainFrame, "TOPLEFT", 0, 0);
				MiDKPDKPPage:SetPoint("BOTTOMRIGHT", MiDKPMainFrame, "BOTTOMRIGHT", 0, 0);
				
				MiDKP_UI.Frame.TabInit();
				MiDKP_UIHelp.General.TabSelectTab(1);

self.used_buttons = {};
self.unused_buttons = {};
self.button_count = 0;
self.class="";
self.single = true;

Scripts.OnDragStop:
MiDKPMiniButtonTexture:SetTexCoord(.075,.925,.075,.925);
MiDKPMiniButton_DragFlag = 0;
self:StopMovingOrSizing();
local _,_,_,xpos,ypos = self:GetPoint();
MiDKP_Config["minibtn_pos"] = {xpos, ypos};

Scripts.OnTextChanged:
if (self.text) then
	self:SetText(self.text);
end
self:SetFocus();
self:HighlightText();

Scripts.OnCursorChanged:
if (self.text) then
	self:SetText(self.text);
end
self:SetFocus();
self:HighlightText();

Scripts.OnShow:
if (self.text) then
	self:SetText(self.text);
end
self:SetFocus();
self:HighlightText();
